using System;
using System.Collections.Generic;
using System.Linq;
using Verifiable.Jwt;
using Xunit;

namespace Verifiable.Tests.DataProviders
{
    /// <summary>
    /// This defines a base class for various predefined JWT test data.
    /// </summary>
    /// <remarks>These vectors are sourced from <see href="https://jwt.io/">jwt.io</see>
    /// for cross-reference purposes. The can be pasted back to the web interface for
    /// easy cross-checking.</remarks>
    public class BaseJwtTestData
    {
        public static Dictionary<string, object> DefaultJwtPayload => new(StringComparer.InvariantCultureIgnoreCase)
        {
            { WellKnownJwtClaims.Sub, "1234567890" },
            { WellKnownJwtClaims.Name, "John Doe" },
            { "admin", true },
            { WellKnownJwtClaims.Iat, 1516239022 }
        };

        public static Dictionary<string, object> AddTypParameter(Dictionary<string, object> header)
        {
            header.Add(JwkProperties.Typ, "JWT");
            return header;
        }


        public Dictionary<string, object> Header { get; }

        public Dictionary<string, object> Payload { get; }

        public string CrossCheckJwt { get; }

        public string PublicKeyInPem { get; }

        public string PrivateKeyInPem { get; }

        public string PrivateKey { get { return PrivateKeyInPem; } }


        public BaseJwtTestData(Dictionary<string, object> header, Dictionary<string, object> payload, string crossCheckJwt, string publicKeyKeyInPem, string privateKeyInPem)
        {
            ArgumentNullException.ThrowIfNull(header);
            ArgumentNullException.ThrowIfNull(payload);
            ArgumentException.ThrowIfNullOrEmpty(crossCheckJwt);
            ArgumentException.ThrowIfNullOrEmpty(publicKeyKeyInPem);
            ArgumentException.ThrowIfNullOrEmpty(privateKeyInPem);

            Header = header;
            Payload = payload;
            CrossCheckJwt = crossCheckJwt;
            PublicKeyInPem = publicKeyKeyInPem;
            PrivateKeyInPem = privateKeyInPem;
        }
    }


    public class ESTestData: BaseJwtTestData
    {
        public ESTestData(Dictionary<string, object> header, Dictionary<string, object> payload, string crossCheckJwt, string publicKeyKeyInPem, string privateKeyInPem) :
            base(header, payload, crossCheckJwt, publicKeyKeyInPem, privateKeyInPem)
        { }
    }


    public class RsaRSTestData: BaseJwtTestData
    {
        public RsaRSTestData(Dictionary<string, object> header, Dictionary<string, object> payload, string crossCheckJwt, string publicKeyKeyInPem, string privateKeyInPem) :
            base(header, payload, crossCheckJwt, publicKeyKeyInPem, privateKeyInPem)
        { }
    }

    public class RsaPSTestData: BaseJwtTestData
    {
        public RsaPSTestData(Dictionary<string, object> header, Dictionary<string, object> payload, string crossCheckJwt, string publicKeyKeyInPem, string privateKeyInPem) :
            base(header, payload, crossCheckJwt, publicKeyKeyInPem, privateKeyInPem)
        { }
    }


    public class HsTestData: BaseJwtTestData
    {
        public HsTestData(Dictionary<string, object> header, Dictionary<string, object> payload, string crossCheckJwt, string privateKey) :
            base(header, payload, crossCheckJwt, privateKey, privateKey)
        {
            //In symmetric schemes there is not difference between public and private key.
        }
    }


